$(document).ready(function(){var a=0;$("#btn-nav-expand").click(function(){if(a==0){$("header .nav-item.nav-main ul").addClass("active");$("#btn-nav-expand").addClass("active");a=1}else{$("header .nav-item.nav-main ul").removeClass("active");$("#btn-nav-expand").removeClass("active");a=0}});$(window).scroll(function(){if($(window).scrollTop()>0){$("header").addClass("active")}if($(window).scrollTop()===0){$("header").removeClass("active")}})});